mapper.update:
	UPDATE OD
    SET    OR_STATUS = 'B'
    WHERE  OR_CODE = #{order[0].orderCode}

mapper.delete:
DELETE
FROM CT
WHERE CT_USCODE = #{userCode} AND CT_PUCODE = #{order[0].product[0].productsCode}

mapper.select:
SELECT PU_SELLCOUNT
FROM PU
WHERE PU_CODE = #{order[0].product[0].productsCode}

mapper.update:
	UPDATE PU
    SET    PU_SELLCOUNT = #{order[0].product[0].productsSellCount}
    WHERE  PU_CODE = #{order[0].product[0].productsCode}

mapper.select:
SELECT US_ACC
FROM US
WHERE US_CODE = #{userCode}

mapper.delete:
UPDATE CP
SET CP_IS = 'x'
WHERE CP_CODE = #{coupon[0].couponCode} AND CP_USCODE = #{userCode}

mapper.update:
UPDATE US
SET US_ACC = #{userAcc}
WHERE US_CODE = #{userCode}

mapper.select:
SELECT COUNT(*)
FROM OC
WHERE OC_USCODE = #{userCode}

    SELECT * FROM (
	    SELECT OC_CODE AS orderCode, OC_DATE AS orderDate,
	    	   OC_PAYMENT AS orderPayment,
	           ROW_NUMBER() OVER (ORDER BY OC_DATE DESC) AS row_num
	    FROM OC
	    WHERE OC_USCODE = #{userCode}
	) temp
	WHERE row_num BETWEEN ${paging[0].startNum} AND ${paging[0].endNum}
	AND orderCode IN (
	    SELECT OR_CODE FROM ORDERS WHERE OR_STATUS = 'B'
	)
